mapper.select:
SELECT user_id, user_pwd
FROM user
WHERE user_id = #{userId}

SELECT user_id
FROM user
WHERE user_name = #{userName}
  AND user_email = #{userEmail}

SELECT COUNT(*)
FROM user
WHERE user_id = #{userId}

SELECT user_pwd
FROM user
WHERE user_name = #{userName}
  AND user_email = #{userEmail}
  AND user_id = #{userId}
